mapper.insert:
INSERT
  INTO
      TB_FILES
      (
      FILE_NO
     ,REF_NO
     ,ORIGINAL_NAME
     ,UPDATE_NAME
     ,FILE_PATH
     ,REF_TYPE
     ,FILE_ANNOTATION
      )
VALUES
      (
      SEQ_FILE_NO.NEXTVAL
     ,#{refNo} + 1
     ,#{originalName}
     ,#{updateName}
     ,#{filePath}
     ,#{refType}
     ,#{fileAnnotation}
      )

	INSERT
	  INTO
	      TB_BOARD
	      (
		  BOARD_NO
		 ,BOARD_TITLE
		 ,BOARD_CONTENT
		 ,MEM_NO
		 )
   VALUES		  
   	     (
   	     SEQ_BOARD_NO.NEXTVAL
   	    ,#{boardTitle}
   	    ,#{boardContent}
   	    ,#{memNo}
   	     )

mapper.update:
UPDATE
      TB_BOARD
   SET
      BOARD_TITLE = #{boardTitle}
     ,BOARD_CONTENT = #{boardContent}
 WHERE
      BOARD_NO = #{boardNo}
