mapper.insert:
INSERT
  INTO
      TB_ATTACHMENT
      (
      FILE_NO
     ,REF_NO
     ,ORIGINAL_NAME
     ,UPDATE_NAME
     ,FILE_PATH
     ,REF_TYPE
     ,FILE_ANNOTATION
      )
VALUES
      (
      SEQ_ATTACHMENT_NO.NEXTVAL
     ,#{refNo} + 1
     ,#{originalName}
     ,#{updateName}
     ,#{filePath}
     ,#{refType}
     ,#{fileAnnotation}
      )

	INSERT
	  INTO
	      TB_BOARD
	      (
		  BOARD_NO
		 ,BOARD_TITLE
		 ,BOARD_CONTENT
		 ,MEM_NO
		 )
   VALUES		  
   	     (
   	     SEQ_BOARD_NO.NEXTVAL
   	    ,#{boardTitle}
   	    ,#{boardContent}
   	    ,#{memNo}
   	     )

mapper.update:
UPDATE
      TB_BOARD
   SET
      BOARD_TITLE = #{boardTitle}
     ,BOARD_CONTENT = #{boardContent}
 WHERE
      BOARD_NO = #{boardNo}

mapper.insert:
INSERT
  INTO
      TB_COMMENT_REPORT
VALUES
      (
      SEQ_COMMENT_REPORT_NO.NEXTVAL
     ,#{reportContent}
     ,SYSDATE
     ,#{refCommentNo}
     , (SELECT MEM_NO
        FROM TB_MEMBER
        WHERE MEM_NICK = #{refMemberNo})
     ,#{memNo}
     ,#{refBoardNo}
      )

INSERT
  INTO
      TB_BOARD_COMMENT
      (
      BOARD_COMMENT_NO
     ,BOARD_COMMENT_CONTENT
     ,BOARD_NO
	 ,MEM_NO
      )
VALUES
	  (
	  SEQ_BOARD_COMMENT_NO.NEXTVAL
	 ,#{commentContent}
	 ,#{boardNo}
	 ,#{memNo}
	  )
